import React, { useEffect, useState } from "react";

const FeedbackForm = () => {
    const [message, setMessage ] = useState('');
    const [email, setEmail ] = useState('')

    useEffect(() => {
      const userEmail = process.env.REACT_APP_EMAIL_USER || 'j.mondragon@hotmail.com';
      console.log('Email from env: ', process.env.REACT_APP_EMAIL_USER);
      setEmail(userEmail)
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const feedback = { 
          email,
          message,
         };
         console.log('Sending feedback:', feedback);

        // Send feedback to the backend

        try {
          const response = await fetch('/api/feedback', {
            method: "POST",
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(feedback),
          });
    
          if (response.ok) {
            const data = await response.text();
            console.log('Response from server:', data);
            setMessage('');
            alert('Thank you for your feedback!');
          } else {
            throw new Error('Failed to send feedback');
          }
        } catch (error) {
          console.error('Error sending feedback:', error);
          alert('An error occurred while sending feedback. Please try again.');
        }
      };

    return (
        <form onSubmit={handleSubmit} className="feedback-form">
          <h2>Send Me Your Feedback</h2>
            <input
              type="hidden"
              id="email"
              name="email"
              value={ email }
              onChange={(e) => setEmail(e.target.value)}
            />
          <div>
            <label htmlFor="message">Message:</label>
            <textarea
              id="message"
              name="message"
              value={ message }
              onChange={(e) => setMessage(e.target.value)}
              required
            />
          </div>
          <button type="submit">Submit</button>
        </form>
      );
};

export default FeedbackForm;
